import { useState } from 'react';

export default () => {
    const [todos, setTodos ] = useState([
        'task 1',
        'task 2',
    ])
    const [todo, setTodo ] = useState([
        ''
    ])

    return (
        <>
            <input value={todo} onChange={(e) => setTodo(e.target.value)}/>
            <button onClick={() => setTodos([...todos, todo ])}>ADD</button>
            {todos.map(todo => 
            (<h1>{todo}</h1>)
            (button)
                )}
        </>
    ) 
}
